mapper.select:
select * 
from (
	select rownum rn, b.*,(
		select count(*) from bbsreply where br_sabun = b_sabun
	) as recnt
	from (
		select * 
		from bbs				
		
			
				where b_name like '%'||#{sval}||'%'
			
			
				where b_title like '%'||#{sval}||'%'
			
			
				where b_content like '%'||#{sval}||'%'
							
		
		order by b_wdate desc	
	) b order by rn
) where rn between #{start} and #{end}
